import React, { Component } from 'react';
import { withRouter } from 'react-router';
import Web3 from 'web3';

class LoginPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      voterId: '',
      password: '',
      error: '',
      loggedIn: false
    };
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = async event => {
    event.preventDefault();
    const { voting, history } = this.props;
    const { voterId, password } = this.state;

    try {
      const voterEligible = await voting.methods.isVoterEligible(voterId).call();
      if (voterEligible) {
        const voterInfo = await voting.methods.getVoterDetails(voterId).call();
        const web3 = new Web3()
        const hashedPassword = web3.utils.sha3(password);
        if (voterInfo.passwordHash !== hashedPassword) {
          console.log('FROM CONTRACT:', voterInfo.passwordHash)
          console.log('FROM sha3', hashedPassword)
          this.setState({ error: 'Invalid password.' });
          return;
        }

        const userHasVoted = await voting.methods.hasVoted(voterId).call();

        if (userHasVoted) {
          this.setState({ error: 'You have already voted.' });
        } else {
          history.push('/voting?voterId='+voterId);
        }
        this.setState({loggedIn: true})
      } else {
        this.setState({ error: 'You are not eligible to vote.' });
      }
    } catch (error) {
      console.error(error);
      this.setState({ error: 'An error occurred. Please try again.' });
    }
  };

  render() {
    const { voterId, password, error } = this.state;

    return (
      <div>
        <h2>Login Page</h2>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Voter ID:</label>
            <input
              type="text"
              name="voterId"
              value={voterId}
              onChange={this.handleInputChange}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={password}
              onChange={this.handleInputChange}
            />
          </div>
          <div>
            <button type="submit">Login</button>
          </div>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    );
  }
}

export default withRouter(LoginPage);
